const ProcessBar = require("progress");
const chalk = require("chalk");

const bar = new ProcessBar("downloading [:bar] :rate/bps :percent :etas", { total: 10});

const timer = setInterval(() => {
    bar.tick();
    if(bar.complete){
        console.log(chalk.blue("Completed!"));
        clearInterval(timer);
    }
}, 100);
